        {/*name field */}
        <Field onChange={(event) => Form.updateField('name', event.target.value)} 
          value={this.state.name} />
        {/*email field */}
        <Field onChange={(event) => Form.updateField('name', event.target.value)} 
          value={this.state.email} />
